@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

#page {
  font-family: system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

.section-container {
  max-width: var(--inner-max-width,800px);
  padding: 4rem 2rem;
}

.content {
  :global(img) {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  :global(p) {
    padding: 0.25rem 0;
    line-height: 1.5;
    font-family: var(--body-font);
    font-weight: 400;
    margin-bottom: 1rem;
    color: #2d3339;
  }

  :global(a) {
    text-decoration: underline;
  }

  :global(h1) {
    font-size: clamp(1.75rem, 10vw, 2.25rem);
    font-weight: 600;
    letter-spacing: -0.08rem;
    margin-bottom: 1rem;
    line-height: 1.1;
  }

  :global(h2) {
    font-size: clamp(1.5rem, 10vw, 2.125rem);
    font-weight: 600;
    margin-bottom: 1rem;
    letter-spacing: -0.06rem;
    line-height: 1.1;
  }

  :global(h3) {
    font-size: clamp(1.25rem, 10vw, 1.75rem);
    font-weight: 600;
    margin-bottom: 0.5rem;
    letter-spacing: -0.05rem;
    line-height: 1.1;
  }

  :global(ul) {
    list-style: disc;
    padding: 0.25rem 0;
    padding-left: 1.25rem;
  }

  :global(ol) {
    list-style: decimal;
    padding-left: 1.25rem;
    margin-bottom: 2rem;
  }

  :global(blockquote) {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

* {
  --icon-size: 30px;
  --max-width: 900px;
}

.section-container {
  display: grid;
  gap: 5rem;
}

.heading-group{
  display: grid;
